import React, { useState } from "react";
import styled from "styled-components";
import classNames from "classnames";
import { SectionProps } from "../../utils/SectionProps";
import ButtonGroup from "../elements/ButtonGroup";
import Button from "../elements/Button";
import Background from "../../assets/images/background.gif";

const propTypes = {
  ...SectionProps.types,
};

const defaultProps = {
  ...SectionProps.defaults,
};

const Container = styled.div`
  background-image: url(${Background});
  width: 100%;
  height: 620px;
  display: flex;
  align-items: center;
  justify-content: center;
`;
const Hero = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  ...props
}) => {
  const [videoModalActive, setVideomodalactive] = useState(false);

  const openModal = (e) => {
    e.preventDefault();
    setVideomodalactive(true);
  };

  const closeModal = (e) => {
    e.preventDefault();
    setVideomodalactive(false);
  };

  const outerClasses = classNames(
    "hero section center-content",
    topOuterDivider && "has-top-divider",
    bottomOuterDivider && "has-bottom-divider",
    hasBgColor && "has-bg-color",
    invertColor && "invert-color",
    className
  );

  const innerClasses = classNames(
    "hero-inner section-inner",
    topDivider && "has-top-divider",
    bottomDivider && "has-bottom-divider"
  );


  return (
    <section {...props} className={outerClasses}>
      <Container>
        <div className={innerClasses}>
          <div className="hero-content">
            <h1
              className="mt-0 mb-16 reveal-from-bottom ta-l"
              data-reveal-delay="200"
            >
              Confinamento Abacaxi Quebrado
            </h1>
            <div className="container-m">
              <p
                className="m-0 mb-32 reveal-from-bottom ta-l"
                data-reveal-delay="400"
              >
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
                eiusmod tempor incididunt ut labore et dolore magna aliqua.
              </p>
              <div className="reveal-from-bottom" data-reveal-delay="600">
                <ButtonGroup>
                  <Button tag="a" color="primary" wideMobile href="#0">
                    Contato
                  </Button>
                  <Button tag="a" color="transparent" wideMobile href="#0">
                    Sobre NÃ³s
                  </Button>
                </ButtonGroup>
              </div>
            </div>
          </div>
        </div>
      </Container>
    </section>
  );
};

Hero.propTypes = propTypes;
Hero.defaultProps = defaultProps;

export default Hero;
