import React from "react";
import classNames from "classnames";

import { SectionTilesProps } from "../../utils/SectionProps";

import { projectsNav } from "../../components/sections/Galery/Data";

/* onst Projects = () => {
  const [item, setItem] = useState({ name: "Foto" });
  const [projects, setProjects] = useState([]);
  const [active, setActive] = useState(0);

  useEffect(() => {
    if (item.name === "Foto") {
      setProjects(projectsData);
    } else {
      const newProjects = projectsData.filter((project) => {
        return project.category.toLowerCase() === item.name;
      });
      setProjects(newProjects);
    }
  }, [item]);

  const handleClick = (e, index) => {
    setItem({ name: e.target.textContent.toLowerCase() });
    setActive(index);
}; */

const propTypes = {
  ...SectionTilesProps.types,
};

const defaultProps = {
  ...SectionTilesProps.defaults,
};

const Testimonial = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  pushLeft,
  typeCarousel,
  setTypeCarousel,
  ...props
}) => {
  const outerClasses = classNames(
    "testimonial section",
    bottomOuterDivider && "has-bottom-divider",
    hasBgColor && "has-bg-color",
    invertColor && "invert-color",
    className
  );

  const innerClasses = classNames(
    "testimonial-inner section-inner",
    bottomDivider && "has-bottom-divider"
  );

  const tilesClasses = classNames("tiles-wrap", pushLeft && "push-left");

  return (
    <section {...props} className={outerClasses}>
      <div className="container">
        <div className={innerClasses}>
          <div className={tilesClasses}>
            <div
              className="tiles-item reveal-from-right"
              data-reveal-delay="200"
            >
              <div className="tiles-item-inner">
                <div className="testimonial-item-content">
                  <h4 className="dark">Sobre</h4>
                  <p className="text-sm mb-0 dark">Nosso gado e história</p>
                </div>
              </div>
            </div>

            <div
              className="tiles-item reveal-from-right"
              data-reveal-delay="200"
              onClick={() => setTypeCarousel("fotos")}
            >
              <div className="tiles-item-inner">
                <div className="testimonial-item-content">
                  <h4 className="text-sm mb-0">Fotos</h4>
                  <p className="text-sm mb-0">Conh0eça nossa fazenda</p>
                </div>
              </div>
            </div>

            <div
              className="tiles-item reveal-from-right"
              data-reveal-delay="200"
              onClick={() => setTypeCarousel("videos")}
            >
              <div className="tiles-item-inner">
                <div className="testimonial-item-content">
                  <h4 className="dark">Vídeos</h4>
                  <p className="text-sm mb-0 dark">Nossos vídeos</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

Testimonial.propTypes = propTypes;
Testimonial.defaultProps = defaultProps;

export default Testimonial;
