import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import styled from "styled-components";
import { Link } from 'react-router-dom';
import FooterSocial from './partials/FooterSocial';

const propTypes = {
  topOuterDivider: PropTypes.bool,
  topDivider: PropTypes.bool
}

const defaultProps = {
  topOuterDivider: false,
  topDivider: false
}

const Nav = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 100%;
  background-color: #000;
`;

const Container = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 100%;
  background-color: #000;
`;

const Footer = ({
  className,
  topOuterDivider,
  topDivider,
  ...props
}) => {

  const classes = classNames(
    'site-footer center-content-mobile',
    topOuterDivider && 'has-top-divider',
    className
  );

  return (
    <footer
      {...props}
      className={classes}
    >

      <div className="container">

        <Nav>
          <div className="footer-nav">
            <h5>
              Informações
            </h5>
            <ul className="list-reset">
              <li>
                <Link to="#0">Início</Link>
              </li>
              <li>
                <Link to="#0">Sobre Nós</Link>
              </li>
              <li>
                <Link to="#0">Trabalhe Conosco</Link>
              </li>
            </ul>
          </div>

          <div className="footer-nav">
            <h5>
              Endereço
            </h5>
            <ul className="list-reset">
              <li>
                <Link to="#0">(66) 0000-0000</Link>
              </li>
              <li>
                <Link to="#0">Avenida 00 - COlider, MT</Link>
              </li>
              <li>
                <Link to="#0">Confinamentoabacaxiquebrado@outlook.com</Link>
              </li>
            </ul>
          </div>
        </Nav>

        <div className={
          classNames(
            'site-footer-inner',
            topDivider && 'has-top-divider'
          )}>

          <div className="footer-top space-between text-xxs">
          </div>

          <div className="footer-bottom space-between text-xxs invert-order-desktop">
            <FooterSocial />

            <div className="footer-copyright">
              © Todos os direitos reservados a ABACAXIQUEBRADO
            </div>
          </div>

        </div>
      </div>
    </footer>
  );
}

Footer.propTypes = propTypes;
Footer.defaultProps = defaultProps;

export default Footer;
