import React, { useState } from 'react';
import styled from "styled-components";
import classNames from 'classnames';
import { SectionSplitProps } from '../../utils/SectionProps';
import { Link } from 'react-router-dom';
import SectionHeader from './partials/SectionHeader';
import Modal from '../elements/Modal';
import Image from '../elements/Image';

const propTypes = {
  ...SectionSplitProps.types
}

const defaultProps = {
  ...SectionSplitProps.defaults
}

const FeaturesSplit = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  alignTop,
  imageFill,
  ...props
}) => {

  const [videoModalActive, setVideomodalactive] = useState(false);

  const openModal = (e) => {
    e.preventDefault();
    setVideomodalactive(true);
  }

  const closeModal = (e) => {
    e.preventDefault();
    setVideomodalactive(false);
  }   

  const outerClasses = classNames(
    'features-split section',
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'features-split-inner section-inner',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider'
  );

  const splitClasses = classNames(
    'split-wrap',
    alignTop && 'align-top'
  );

  const sectionHeader = {
    title: 'Sobre a Empresa',
    paragraph: 'Nós do confinamento abacaxi quebrado temos o orgulho de sermos um grande diferencial, estando focado no compromisso com o meio ambiente, com muita dedicação e bem estar aos animais, e assim trabalhando com sistemas  automatizados para a distribuição e nutrição animal nos setores de baias preparadas para confinação bovina.'
  };

  return (
    <section {...props} className={outerClasses}>
      <div className="container">
        <div className={innerClasses}>
          <SectionHeader data={sectionHeader} className="center-content"></SectionHeader>
          <div className={splitClasses}>


{/*             <div className="split-item">
              <div className="split-item-content center-content-mobile reveal-from-right" data-reveal-container=".split-item">
                <p className="m-0">

                </p>
              </div>
            </div> */}

            <div className="split-item">

              <div className="split-item-content center-content-mobile reveal-from-left" data-reveal-container=".split-item">

                <div className="text-xxs text-color-primary fw-600 tt-u mb-8">
                Nosso Gado
                </div>

                <p className="m-0">
                  O confinamento abacaxi quebrado possue um sistema de produção de gado de corte para o abastecimento, com carne de origem segura e padrão de qualidade constante,
                  tanto para o mercado interno quanto para a exportação.
                </p>

                <br/>

                <p className="m-0">
                  No momento em que os animais oriundos de compras, chegam ao confinamento, eles passam por um tratamento sanitário profilático para garantir a devida imunização contra
                  algumas enfermidades e parasitas que possam a cometer sua saúde, feito isso os animais são separados em lotes por idade, tamanho e peso padrão para que sejam direcionados as devidas baias onde começam a ser suplementados com ração.
                </p>

                <p style={{ display:"none" }}> aaa </p>
                
                <Link style={{color: "grey", cursor: "pointer"}}> Ver Mais... </Link>

              </div>

              <div className="hero-figure reveal-from-bottom" data-reveal-value="20px" data-reveal-delay="800">
                  <a
                    data-video="https://player.vimeo.com/video/740134746"
                    href="#0"
                    aria-controls="video-modal"
                    onClick={openModal}
                  >
                    <Image
                      className="has-shadow"
                      src={require('./../../assets/images/video-placeholder.png')}
                      alt="Hero"
                      width={896}
                      height={504} />
                  </a>
                </div>
                <Modal
                  id="video-modal"
                  show={videoModalActive}
                  handleClose={closeModal}
                  video="https://player.vimeo.com/video/740134746"
                  videoTag="iframe" />
              </div>

              <div className="text-xxs text-color-primary fw-600 tt-u mb-8">
                História
              </div>

              <p className="m-0">
                Com mais de dez anos no seguimento de confinamento de gado, uma parceria entre Flavio Martins Simone e Moises Maciel Veiga Ribeiro
                ao longo desses anos atingindo a sua meta de 22.500,00 mil animais estáticos. No ano de 2021 abatendo 42.000.00 mil animais,
                e nesse ano de 2022 ate o mês de julho foram abatidos 32.000,00 mil bovinos. O confinamento trabalha com cem por cento de rebanho próprio, não trabalhamos com boi tell
                O nosso compromisso e como bem estar dos animais, o meio ambiente, com os nossos colaboradores e fornecedores.
              </p>

          </div>
        </div>
      </div>
    </section>
  );
}

FeaturesSplit.propTypes = propTypes;
FeaturesSplit.defaultProps = defaultProps;

export default FeaturesSplit;
