import React, { useState } from 'react';
import classNames from 'classnames';
import { SectionTilesProps } from '../../utils/SectionProps';
import SectionHeader from './partials/SectionHeader';
import Modal from '../elements/Modal';
import Image from '../elements/Image';

const propTypes = {
  ...SectionTilesProps.types
}

const defaultProps = {
  ...SectionTilesProps.defaults
}

const Testimonial = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  pushLeft,
  ...props
}) => {

  const [, setVideomodalactive] = useState(false);

  const openModal = (e) => {
    e.preventDefault();
    setVideomodalactive(true);
  }


  const outerClasses = classNames(
    'feature section',
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'section-inner',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider'
  );

  const tilesClasses = classNames(
    'tiles-wrap',
    pushLeft && 'push-left'
  );

  const sectionHeader = {
    title: 'Titulo estático',
    paragraph: 'Esta seção está em desenvolvimento'
  };

  return (
    <section
      {...props}
      className={outerClasses}
    >
      <div className="container">
        <div className={innerClasses}>
          <SectionHeader data={sectionHeader} className="center-content" />
          <div className={tilesClasses}>

            <div className="tiles-item reveal-from-right" data-reveal-delay="200">
              <div className="tiles-item-inner">
                <div className="hero-figure reveal-from-bottom" data-reveal-value="20px" data-reveal-delay="800">

                  <a>
                    <Image
                      onClick={openModal}
                      className="has-shadow"
                      src={require('./../../assets/images/gado1.png')}
                      alt="Hero"
                      width={900}
                      height={510} />
                  </a>

                </div>

                  <Modal
                    src={require('./../../assets/images/gado1.png')}
                  />

                </div>
            </div>

            <div className="tiles-item reveal-from-right" data-reveal-delay="200">
              <div className="tiles-item-inner">
                <div className="hero-figure reveal-from-bottom" data-reveal-value="20px" data-reveal-delay="800">

                  <a>
                    <Image
                      onClick={openModal}
                      className="has-shadow"
                      src={require('./../../assets/images/gado2.png')}
                      alt="Hero"
                      width={900}
                      height={510} />
                  </a>

                </div>

                  <Modal
                    src={require('./../../assets/images/gado2.png')}
                  />

                </div>
            </div>

            <div className="tiles-item reveal-from-right" data-reveal-delay="200">
              <div className="tiles-item-inner">
                <div className="hero-figure reveal-from-bottom" data-reveal-value="20px" data-reveal-delay="800">

                    <a>
                      <Image
                        onClick={openModal}
                        className="has-shadow"
                        src={require('./../../assets/images/gado3.png')}
                        alt="Hero"
                        width={900}
                        height={510} />
                    </a>

                  </div>

                    <Modal
                      src={require('./../../assets/images/gado3.png')}
                    />

                </div>
            </div>

          </div>
        </div>
      </div>
    </section>
  );
}

Testimonial.propTypes = propTypes;
Testimonial.defaultProps = defaultProps;

export default Testimonial;
